import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import { useMemo } from "react";



const searchResults = [
  {
  "url": "yandex.ru",
  "title": "cars",
  "text": "i like cars very much",
  "image": "https://avtorinok.ru/photo/pics/aston-martin/v8-vantage/98969.jpg"
  },
  {
  "url": "vk.ru",
  "title": "planes",
  "text": "i like planes not so much",
  "image": "https://i.artfile.ru/5060x2154_1345763_[www.ArtFile.ru].jpg"
  },
  {
  "url": "google.com",
  "title": "trains",
  "text": "i like trains",
  "image": "https://cq.ru/storage/uploads/posts/1680038/1.jpg"
  }
]

function ResultCard({result}) {
    return (
      <li>
        <a href={result.url}>link</a>
        <h1>{result.title}</h1>
        <p>{result.text}</p>
        <img src={result.image}></img>
      </li>
    )
}

function SearchField({getNewResult}) {
  const [searchQuery, setSearchQuery] = useState("");

  function updateText(event) { setSearchQuery(event.target.value)}
  function updateResultWithQuery() { getNewResult(searchQuery) }

  return (
    <div>
      <input onChange={updateText}/>
      <button onClick={updateResultWithQuery}> search </button>
    </div>
  )
}

function ResultListCard({resultList}) {
    let listElements = resultList.map( result=>
      <ResultCard result={result}/>
    )
    return (
      <ol>{listElements}</ol>
    )
}

function App() {
  const [sentResults, setSentResults] = useState([]);


  function getNewResult(onChange) {
    setSentResults([...sentResults, {"title": onChange}])


  }

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: "AIzaSyD2LhLsaPLxdu52uAXd3EKJXdNyr3oFwOg",
  });
  const center = useMemo(() => ({ lat: 18.52043, lng: 73.856743 }), []);

  return (
    <div className="App">
      {!isLoaded ? (
        <h1>Loading...</h1>
      ) : (
        <GoogleMap
          mapContainerClassName="map-container"
          center={center}
          zoom={10}
        />
      )}
      <SearchField getNewResult={getNewResult}/>
      <ResultListCard resultList={sentResults}/>
    </div>
  );
}

export default App;
